      // adjective
      // </Typography>
      // <Typography component="p">
      // well meaning and kindly.
      // <br />
      // {'"a benevolent smile"'}
      // </Typography>
      // </CardContent>
      // <CardActions>
      // <Button size="small">Learn More</Button>
      // </CardActions>
      // </Card>
      <div>
      <i className="fas fa-clock"></i>
      <span>{this.state.date.toLocaleTimeString()}</span>
      </div>
    );
  }
}
export default withStyles(styles)(Clock);
